import React, { useContext, useEffect, useState } from 'react';
import { MyContext } from '../provider/MyProvider';
import requestAPI from '../util/requestAPI';
import './principal.css';

export default function FavoritePokemons() {
  const pokemonsList = JSON.parse(localStorage.getItem('favoritePokemons'));
  const { favoritePage, setFavoritePage } = useContext(MyContext);

  useEffect(() => {
    async function FavoritePokemonsGet() {
      const result = await pokemonsList.map((name) => {
        return requestAPI(`https://pokeapi.co/api/v2/pokemon/${name}`);
      });
      setFavoriteAPI(result);
    }
    FavoritePokemonsGet();
  }, [pokemonsList]);

  return (
    <div className="app">
      { console.log(favoriteAPI) }
    </div>
  );
}
