import React, { useContext, useState } from 'react';
import { MyContext } from '../provider/MyProvider';
import requestAPI from '../util/requestAPI';

export default function PokemonCard() {
  const { dataAPI } = useContext(MyContext)
  const [picture, setPicture] = useState('');

  async function requestPicturePokemon(url) {
    const result = await requestAPI(url);
    setPicture(result.sprites.front_default);
  }

  return (
    <div>
      { dataAPI.results && dataAPI.results.map((pokemon, index) => {
        // requestPicturePokemon(pokemon.url);
        return (
          <div key={ index }>
            <p>{ pokemon.name }</p>
            <img src={ picture } alt="pokemon.png" />
          </div>
        )
      })}
      {console.log(dataAPI)}
    </div>
  );
}
