import React, { useEffect, useState } from 'react';
import requestAPI from '../util/requestAPI';
import Modal from 'react-modal';
import { GrClose } from "react-icons/gr";
import { customStyles } from '../util/styles';

export default function PokemonCard({name, url}) {
  const [picture, setPicture] = useState('');
  const [pokemonModal, setPokemonModal] = useState([]);
  const [modalIsOpen, setIsOpen] = useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function closeModal() {
    setIsOpen(false);
  }

  useEffect(() => {
    async function getPokemon() {
      const result = await requestAPI(url);
      setPicture(result.sprites.front_default);
    }
    getPokemon();
  }, [url]);

  async function getPokemon(pokemonName) {
    const result = await 
      requestAPI(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`);
    setPokemonModal(result);
  }

  return (
    <div>
      <div onClick={ () => {
        openModal();
        getPokemon(name);
      } }>
        <img src={ picture } alt="pokemon-phot" />
        <p>{ name }</p>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <GrClose onClick={ closeModal } />
      </Modal>
    </div>
  );
}
